import { createContext, useState } from "react";

// Data
import FeedbackData from "../"

const FeedbackContext = createContext();

export const FeedbackProvider = ({ children }) => {
    const [feedback, setFeedback] = useState([
        {
            id: 1,
            rating: 10,
            text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Quisque dictum nisl et lacus commodo luctus. Aenean non mi sed odio laoreet volutpat a non turpis. Curabitur sed lectus vitae mi tempus lobortis.",
        },
        {
            id: 2,
            rating: 9,
            text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Quisque dictum nisl et lacus commodo luctus. Aenean vehicula, purus in cursus luctus, enim odio fermentum ligula, dictum mollis ligula dolor non purus. ",
        },
        {
            id: 3,
            rating: 7,
            text: "Lorem ipsum dolor sit amet, consectetur adipiscing elit. Quisque dictum nisl et lacus commodo luctus. dictum mollis ligula dolor non purus. Aenean non mi sed odio laoreet volutpat a non turpis. Curabitur sed lectus vitae mi tempus lobortis.",
        },
    ]);

    const deleteFeedback = (id) => {
        if (window.confirm("Are you sure you want to delete?")) {
            setFeedback(feedback.filter((item) => item.id !== id));
        }
    };

    return (
        <FeedbackContext.Provider value={{ feedback, deleteFeedback }}>
            {children}
        </FeedbackContext.Provider>
    );
};

export default FeedbackContext;
