    }; */

    return (
        <FeedbackProvider>
            <Header />
            <div className="container">
                <FeedbackForm />
                {/* <FeedbackStats /> */}
                <FeedbackList />
            </div>
        </FeedbackProvider>
    );
};

export default App;
